package java_basics;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 100;
		
		do {
			System.out.println(x);
			x++;
		} while (x <= 100);
		
//		while(x <= 100) {
//			System.out.println(x);
//			x++;
//		}
		//System.out.println("Out of while loop");
	}

}
